import React from "react";
import { Form } from "react-bootstrap"
import {connect} from "react-redux";

class Filter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
    }

    async componentDidMount(){
       let products = await this.props.products;
       this.setState({data: products});
    //    this.categoryFilter();
    }

    async categoryFilter(){
        const type = radio 
        await this.state.data.map((value, index) => {
            return (
                <Form.Check label={value.category}  id={`inline-${type}-1`} />
            )
        })
    }

    render() {
        return (
            <Form className="mt-3">
                <Form.Group controlId="exampleForm.ControlSelect2">
                    <Form.Label>Filter</Form.Label>
                    <Form.Control as="select">
                        <option>Default</option>
                        <option>Price low</option>
                        <option>Price High</option>
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Category</Form.Label>
                    {['radio'].map((type) => (
                        <div key={`default-${type}`} className="mb-3">
                            {this.categoryFilter()}
                            {/* <Form.Check label="Tech" type={type} id={`inline-${type}-1`} />
                            <Form.Check label="Clothes" type={type} id={`inline-${type}-2`} />
                            <Form.Check label="Diverse" type={type} id={`inline-${type}-3`} /> */}
                        </div>
                    ))}
                </Form.Group>
            </Form>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Filter);
