import React from 'react';
import axios from "axios";
import {connect} from "react-redux";


class Admin extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            datas: [],
            list:[],
            buttonModify: false
        }

        this.listUser = this.listUser.bind(this);
        this.updateUser = this.updateUser.bind(this);
        this.sendUpdate = this.sendUpdate.bind(this);
    }

    async componentDidMount(){
        let dataUser = await this.props.datas;
        localStorage.setItem("keepState", JSON.stringify(dataUser));
        // console.log(JSON.parse(localStorage.getItem("keepState")));
        this.setState({datas: JSON.parse(localStorage.getItem("keepState"))});
        this.listUser();
    }

    async listUser(){
        
        let list =  await this.state.datas.map((value, index) => { 
            return (
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                            <td>
                                <button onClick={(e) => {this.setState({buttonModify: true});this.updateUser();}}>Modifier</button>
                            </td>
                    </tr>
                );
            });
        this.setState({list:list});
    }

    updateUser(){
       let UpdateList =  this.state.datas.map((value, index) => {
            return(
                <tr key={index}>
                    <th>{value._id}</th>
                    <th>{value.mail}</th>
                    <td>
                        <form onSubmit={this.sendUpdate}>
                            <input  type="hidden" value= {value._id} name="id"/>  
                            <input  type="text"  name="password"/>
                            <input  type="submit"  value="envoyer"/>    
                        </form>
                        
                    </td>
                </tr>);
        })
        this.setState({list: UpdateList})
    }

    async sendUpdate(e){
        e.preventDefault();
        console.log( e.target.password.value)
        let data = { 
            id: e.target.id.value,
            password: e.target.password.value
            
        }
        await axios.put("http://localhost:1234/loggin/update", data).then(response => { 
            console.log(response)
        })
        .catch(error => {
            console.log(error.response)
        });;
    }

   
    render(){
        console.log(this.state.buttonModify)
        return(
            <table>
                <tbody>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    {this.state.list}
                </tbody>
            </table>
                

        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }
  export default connect(mapStateToProps)(Admin);
