    //                 </td>
    //             </tr>);
    //     })
    //     this.setState({list: UpdateList})
    }

    listUser(datas){
        let list =  datas.map((value, index) => { 
            return (
                <tr key={index}>
                    <th>{value._id}</th>
                    <th>{value.mail}</th>
                    { this.state.buttonModify &&
                        <td>
                            <input onClick={(e) => this.updateUser(e, value._id)} name="password" value={value.password}/>
                        </td>
                    }
                    { !this.state.buttonModify &&
                        <td>
                            <button onClick={() => {this.setState({buttonModify: true})}}>Modifier</button>
                        </td>
                    }
                    
                </tr>
            );
            });
        this.setState({list:list});
    }
    render(){
        console.log()
        return(
            <table>
                <tbody>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    {this.state.list}
                </tbody>
            </table>
                

        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }
  export default connect(mapStateToProps)(Admin);
