import React from 'react';
import {connect} from "react-redux";
import { addProductInCart } from '../actions/actions';
import { Container, Card, Button,  Row, Col} from 'react-bootstrap';
import { Link } from 'react-router-dom';


class User extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            list: [],
            cart: this.props.cart
        }
    }

    componentDidMount(){
        this.listCart();
    }

    async listCart(){
        console.log();
        let cart = await this.state.cart;
        let list = cart.map((value, index) => {
            return(
                <Col key={index} sm={4} md={2}>
                    <Card className="border border-secondary mt-3 mr-3"  style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                        <Card.Body>
                            <Link  to={"/article/"+value._id} className="p-0 text-dark">{value.article}</Link>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>Quantity: {value.quantity}</Card.Text>
                            <Button onClick={() => {this.deleteArticleInCart(value)}} variant="primary">Delete</Button>
                            
                        </Card.Body>
                    </Card>
                </Col>
            )
       });
       	console.log(list);
       this.setState({list: list})

    }
        
    
    
    render(){
        console.log(this.state.list)
        return(
            <Container>
                <Row>
                    {this.state.list}           
                </Row>
            </Container>
            
        )
        
    }
}

function mapStateToProps(state){
    return {
      cart: state.cart
    };
  }

function mapDispatchToProps(dispatch) {
  return { deleteArticle: (data) => {dispatch(addProductInCart(data))} }
}
export default connect(mapStateToProps, mapDispatchToProps)(User);
