import React, { useState } from "react";
import { withRouter } from "react-router-dom";
import {connect} from "react-redux";
import {Container, Row, Col, Card, Button} from 'react-bootstrap';


function getCardArticle(props){
    const [data, setData] = useState(props.product);

    async function Display() {
        await data.map((value, index) => {
            if (this.state.category === (undefined || "all")) {
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo} style={{ width: "auto", height: "200px" }} />
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                )
            }
            if (this.state.category === value.category) {
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo} style={{ width: "auto", height: "200px" }} />
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                )
            }
            if (this.state.category === value.article.toLowerCase()) {
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo} style={{ width: "auto", height: "200px" }} />
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                )
            }
            // console.log(this.state.category);
            if (this.state.sortArray) {
                console.log("sort")
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo} style={{ width: "auto", height: "200px" }} />
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                )
            }
        }
        );
    }
}

function mapStateToProps(state) {
    return ({
        Product: state.product
    });
};

export default connect(mapStateToProps, null)(withRouter(getCardArticle))
