import React from 'react';
import axios from "axios";
import {connect} from "react-redux";


class Admin extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            datas: [],
            list:[],
            buttonModify: false
        }

        this.listUser = this.listUser.bind(this);
        this.updateUser = this.updateUser.bind(this);
        this.sendUpdate = this.sendUpdate.bind(this);
    }

    async componentDidMount(){
        let datas = await this.props.datas;
        this.setState({datas:datas});
        this.listUser();
    }

    listUser(){
        let list =  this.state.datas.map((value, index) => { 
            return (
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                            <td>
                                <button onClick={(e) => {this.setState({buttonModify: true});this.updateUser();}}>Modifier</button>
                            </td>
                    </tr>
                );
            });
        this.setState({list:list});
    }

    updateUser(){
       let UpdateList =  this.state.datas.map((value, index) => {
            return(
                <tr key={index}>
                    <th>{value._id}</th>
                    <th>{value.mail}</th>
                    <td>
                        <form onSubmit={this.sendUpdate}>
                            <input  type="text" value={value.password} name="password"/>
                            <input  type="submit"  value="envoyer"/>    
                        </form>
                        
                    </td>
                </tr>);
        })
        this.setState({list: UpdateList})
    }

    sendUpdate(){

    }

   
    render(){
        console.log(this.state.buttonModify)
        return(
            <table>
                <tbody>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    {this.state.list}
                </tbody>
            </table>
                

        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }
  export default connect(mapStateToProps)(Admin);
