import React from 'react';
import axios from "axios";
import {connect} from "react-redux";
import { deleteUser } from '../actions/actions';
import Product from './Product';
import User from './User';
import * as serviceUser from "../services/serviceUser";


class Admin extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            datas: [],
            listUser:[],
            buttonModify: false
        }

        this.listUser = this.listUser.bind(this);
        this.updateListUser = this.updateListUser.bind(this);
        this.sendUpdate = this.sendUpdate.bind(this);
        this.removeUser = this.removeUser.bind(this);
    }

    async componentDidMount(){
        let users = await this.props.users;
        console.log(users);
        this.setState({datas:users});
        this.listUser();
    }

    async listUser(){
        
        let list =  await this.state.datas.map((value, index) => { 
            return (
                    
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                        <td>
                            <button onClick={this.updateListUser}>Modify</button>
                        </td>
                        <td>
                            <button onClick={() => {this.removeUser(value._id)}}>Delete</button>
                        </td>
                    </tr>
                        
                );
            });
            this.setState({listUser:list});
    }

    removeUser(id){
        var tab = [];
        let dataUsers = this.state.datas;
        tab.push(id);
        for (var i = 0; i < dataUsers.length; i++) {
            var obj = dataUsers[i];

            if (tab.indexOf(obj._id) !== -1) {
                dataUsers.splice(i, 1);
            }
        }
        
        this.props.actions(id, dataUsers);
        this.setState({data: dataUsers});
        this.listUser();
    }

    async updateListUser(){
       
        let list =  await this.state.datas.map((value, index) => { 
            return (
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                        <td>
                            <form onSubmit={(e) => {this.sendUpdate(e);this.listUser();}}>
                                <input  type="hidden" defaultValue= {value._id} name="id"/>  
                                <input  type="text"  name="password"/>
                                <input  type="submit"  value="Update"/>    
                            </form>
                            
                        </td>
                    </tr>
                );
            });
            this.setState({listUser:list});
    }

    async sendUpdate(e){
        serviceUser.updateUser(e)
    }

   
    render(){
        console.log(this.state.buttonModify);
                return(
            <div>
                
                {this.state.buttonModify === true ?
                    <div>
                        <button onClick={() => this.setState({buttonModify:false})}>Panel products</button>
                        <User/>
                    </div>
                :
                <div>
                    <button onClick={() => this.setState({buttonModify:true})}>Panel users</button>
                    <Product/>
                </div>
                }
            </div>
            
        )
    }
}

function mapStateToProps(state){
    return {
      users: state.user,
    };
  }

function mapDispatchToProps(dispatch) {
  return { actions: (id, data) => {dispatch(deleteUser(id, data))} }
}
  export default connect(mapStateToProps, mapDispatchToProps)(Admin);
