import React from "react";
import axios from "axios";

class Product extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            listArtcile:[]
        }
    }
    async componentDidMount(){
        let articles = await axios.get("http://localhost:1234/product/read");
        console.log(articles);
        let listArtcile = articles.data.map((value, index) => {
            return(
                <tr key={index}>
                    <td>{value.id}</td>
                    <td>{value.article}</td>
                    <td>{value.price}</td>
                    <td>{value.photo}</td>
                    <td><button>Modify</button></td>
                    <td><button>Delete</button></td>
                </tr>
            )
        })
        this.setState({listArtcile:listArtcile});
    }

    render(){
        return(
            <div>
                <table>
                    <tbody>
                        {this.state.listArtcile}
                    </tbody>
                </table>
                
            </div>
            
        )
    }
}

export default Product;
