import { withRouter } from "react-router-dom";
import {connect} from "react-redux";
import {Container, Row, Col, Card, Button} from 'react-bootstrap';


 class GetCardArticle extends React.Component{
    constructor(props){
        super(props);
        this.State = {
            list: []
        }
    }

   async displayAll(){
        if(this.state.category === (undefined || "all")){
