            datas: [],
            list:[],
            buttonModify: false
        }

        this.listtUser = this.listUser.bind(this);
        this.updateUser = this.updateUser.bind(this);
    }

    async componentDidMount(){
        this.listUser()
    }

    async listUser(){
        let datas = await this.props.datas;
        let list =  datas.map((index, value) => { 
            return (
            <tr key={index}>
                <th>{value._id}</th>
                <th>{value.mail}</th>
                {this.state.buttonModify && 
