import React from "react";
import { connect } from "react-redux";
import "../style/Acceuil.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';

class Acceuil extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            list: []
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount() {
        let products = await this.props.products;
        this.setState({ data: products });
        this.listData();

    }

    async listData() {
        let list = this.state.data.map((value, index) => {
            console.log(value);
            return (
                <div className="card" key={index}>
                    <img src={"/img/" + value.photo} alt="image" style={{ width: "200px", height: "200px" }} />
                    <p>{value.article}</p>
                    <p>{value.price}$</p>
                </div>
            )
        });

        this.setState({ list: list })
    }


    render() {
        console.log(this.state.list);
        return (
            <Container>
                <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
                    <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="#features">Features</Nav.Link>
                            <Nav.Link href="#pricing">Pricing</Nav.Link>
                        </Nav>
                        <Nav>
                            <Nav.Link >More deets</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
                <h3>Bienvenue</h3>
                <div className="flex">
                    {this.state.list}
                </div>
            </Container>

        )
    }

}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Acceuil);
