import React from "react";
import {connect} from "react-redux";
import Product from "./Product";

class Acceuil extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            data: [],
            list:[]
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount(){
       let products = await this.props.products;
       this.listData();
       this.setState({data:products});
       
    }

    async listData(){
        let list = this.state.data.map((value, index) => {
            return(<li key={index}>{value.article}</li>)
        });
        
        this.setState({list: list})
    }


    render(){
        return(
            <div>
                <ul>
                    {this.state.list}
                </ul>
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Acceuil);
