import * as actionType from "../actions/actionType"
import axios from "axios";
import { combineReducers } from "redux";

const deleteUserById = async (id) => {
    let data = {
        id: id
    }
    
    await axios.delete("http://localhost:1234/loggin/delete", {data});
   
}

const reducer_user = async (state = {}, action) => {
    let newState;
    switch(action.type){
        case actionType.DELETE_USER:
            deleteUserById(action.id);
            newState = action.data;
            return newState;
        case actionType.SHOW_USER:
            newState = action.data;
            return newState;
        default:
        return state;
    }
}

const reducer_product = async (state = {}, action) => {
    let newState;
    switch(action.type){
        case actionType.SHOW_PRODUCT:
            newState = action.data;
            return newState;
        default:
        return state;
    }
}

const userApp = combineReducers({
    user: reducer_user,
    product: reducer_product
})



export default userApp;
