import React from 'react';
import axios from "axios"
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Admin from './Admin';
import{connect} from "react-redux"
import { showUser } from '../actions/actions';
import { bindActionCreators } from 'redux';


class User extends React.Component {
    constructor(props){
        super(props);

        this.sendForm = this.sendForm.bind(this);
        this.connect = this.connect.bind(this);

        
        this.state = {
            page: true,
            mail:"",
            password:"",
            datas: [],
            verify:""

        }
        
    }

    async componentDidMount(){
        let datasUsers = await axios.get("http://localhost:1234/loggin/read");

        await datasUsers.data.map((value, index) => { return this.state.datas.push({id: value._id, index:index, mail: value.mail, password: value.password})})
        console.log(this.state.datas);
        
        // this.setState({datas: list});
        
    }

   async sendForm(e){
       e.preventDefault();

       let data = {
           mail: this.refs.mail.value,
           password: this.refs.password.value
       }
        
        await axios.post("http://localhost:1234/loggin/create", data);
        console.log(await axios.get("http://localhost:1234/loggin/read"));
        this.setState({page: "login", 
                        mail: this.refs.mail.value, 
                        password: this.refs.password.value});
    
    }

    

    async connect(e){
        e.preventDefault();
        console.log(this.state.dats);
        this.props.dispatch(showUser(this.state.datas));
        let data = {
            mail: this.refs.mail.value,
            password: this.refs.password.value
        }
        let verify = await axios.post("http://localhost:1234/loggin/verify", data);
        
        if(verify){
            console.log(verify);
            this.setState({page:null, verify: verify});
        }

    }

    register(){
      return ( 
      <div> 
          {this.state.page === true && 
            <div>
                <form onSubmit={this.sendForm}>
                    <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                    <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="text"/></Col>
                    <Col xs md lg="10"><input placeholder="password" name="passwordVerif" ref="passwordVerif" type="password"/></Col>
                    <Button type="submit">Envoyer</Button>
                </form>
                <Button type="button" onClick={() => { this.setState({page: false})}}>Loggin</Button>
            </div>
          }
          {this.state.page === false && 
                <div>
                    <form onSubmit={this.connect}>
                        <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                        <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="password"/></Col>
                        <Button type="submit">Envoyer</Button>
                    </form>
                    <Button type="button"  onClick={() => { this.setState({page: true})}}>Register</Button>
                </div>
            }
            {this.state.verify.data === "admin" && 
            <Admin/>
            }
            {this.state.verify.status === 200 && this.state.verify.data !== "admin" && 
            <div>Aller sur la page product</div>
            }
        </div>)
        
    }
    
    render(){
        return(
            <div>
                <p>Component Users</p>
                <Container>
                    <Row>
                    
                        {this.register()}
                    </Row>
                    
                </Container>
            </div>
        );
    }
}

// function mapDispatchToProps(dispatch) {
//   return { actions: () => {dispatch(showUser())} }
// }
// export default connect(null, mapDispatchToProps)(User);

function mapDispatchToProps(dispatch) {
    return { actions: bindActionCreators(showUser, dispatch) }
  }
  
  export default connect(mapDispatchToProps)(User);
