import React from "react";
import { connect } from "react-redux";
import "../style/Acceuil.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, Row, Col, Card, Button} from 'react-bootstrap';
import Header from "./Header";
import Filter from "./Filter";


class Accueil extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            list: [],
            category: "",
            sortArray:["Price low", "Price high"]
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount() {
        let products = await this.props.products;
        this.setState({ data: products, category: this.props.category });
        await this.listData();
    }

    async componentWillReceiveProps(nextProps) {
        // console.log(nextProps)
        
        if(nextProps.category === this.state.sortArray[0]){
            await this.listSortData();
        }else if(nextProps.category === this.state.sortArray[1]){
            await this.listSortData();
        }
        else{
            await this.setState({category: nextProps.category});
            await this.listData();
        }
    }

    async listSortData(){
        let list = await this.state.data.sort((a, b) => {
            if(a.price < b.price){
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                ) 
            }
        })
        console.log(list);
        this.setState({list: list})
    }

    async listData() {
        
        let list = await this.state.data.map((value, index) => {
            if(this.state.category === (undefined || "all")){
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                ) 
            }
            if(this.state.category === value.category){
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                ) 
            }
            if(this.state.category === value.article.toLowerCase()){
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={window.location.origin + "/img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                ) 
            }

            // console.log(this.state.category);
                
        });
        this.setState({ list: list })
    }

    render() { 

        return (
            <div>
                <Header />
                <Container fluid>
                    <Row>
                        <Col xs="2" sm="2" md="3" lg="2" className="border border-secondary mt-3 ml-3"><Filter/></Col>
                        <Col><div className="flex">{this.state.list}</div></Col>
                    </Row>
                </Container>
            </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Accueil);
