import React from "react";
import axios from "axios";

class Product extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            listArtcile:[],
            data:[]
        }
        this.updateListArticle = this.updateListArticle.bind(this);
        this.updateProduct = this.updateProduct.bind(this);
        this.deleteProduct = this.deleteProduct.bind(this);
    }
    async componentDidMount(){
        let articles = await axios.get("http://localhost:1234/product/read");
        console.log(articles);
        let listArtcile = articles.data.map((value, index) => {
            return(
                <tr key={index}>
                    <td>{value._id}</td>
                    <td>{value.article}</td>
                    <td>{value.price}</td>
                    <td>{value.photo}</td>
                    <td><button onClick={this.updateListArticle}>Modify</button></td>
                    <td><button>Delete</button></td>
                </tr>
            )
        })
        this.setState({listArtcile:listArtcile, dataArticle:articles.data});
    }

    updateListArticle(){
        this.state.dataArticle.map((value, index) => {
            return(
                <tr key={index}>
                    <td>
                        <form onSubmit={this.updateProduct}>
                            <input name="id" type="text" placeholder={value._id} value={value._id}/>
                            <input name="article" type="text" placeholder={value.article} value={value.article}/>
                            <input name="price" type="text" placeholder={value.price} value={value.price}/>
                            <input name="photo" type="text" placeholder={value.photo} value={value.photo}/>
                            <input type="submit" value="Update"/>
                        </form>
                    </td>
                    <td><button onClick={() => {this.deleteProduct(value._id)}}>Delete</button></td>
                </tr>
            )
        })

        this.setState({listArtcile:listArtcile});
    }

    updateProduct(e){
        e.preventDefault();
        console.log(e.target.id.value)
    }

    deleteProduct(id){
        console.log(id);
    }

    render(){
        return(
            <div>
                <table>
                <thead>
                        <tr>
                            <th>Id</th>
                            <th>article</th>
                            <th>price</th>
                            <th>photo</th>
                        </tr>
                        
                    </thead>
                    <tbody>
                        {this.state.listArtcile}
                    </tbody>
                </table>
                
            </div>
            
        )
    }
}

export default Product;
