import React from 'react';
import axios from "axios"
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';


export default class Product extends React.Component {
    constructor(props){
        super(props);

        this.sendForm = this.sendForm.bind(this);

        this.state = {sendBool: false}
        
    }

    async componentDidMount(){
        console.log(await axios.get("http://localhost:1234/loggin/read"))
    }

   async sendForm(e){
       e.preventDefault();
    
       let data = {
           mail: this.refs.mail.value,
           password: this.refs.password.value
       }
        console.log(e);
        await axios.post("http://localhost:1234/loggin/create", data)
        console.log(await axios.get("http://localhost:1234/loggin/read"))
    
    }

    async loggin(e){
        let dataUser = await axios.get("http://localhost:1234/loggin/read");

        dataUser.map((value, index) => {
            // if(){

            // }
        });

        this.setState({sendBool: true});
    }


    render(){
        return(
            <div>
                <p>Component Users</p>
                <Container>
                    <Row>
                    { this.state.bool != true ?
                        <form onSubmit={this.sendForm}>
                            <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                            <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="text"/></Col>
                            <Col xs md lg="10"><input placeholder="password" name="passwordVerif" ref="passwordVerif" type="text"/></Col>
                            <Button type="submit">Envoyer</Button>
                        </form>
                        :
                        <div>
                            <form onSubmit={this.loggin}>
                            <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                            <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="text"/></Col>
                            <Button type="submit">Envoyer</Button>
                        </form>
                        </div>
                    }
                        
                    </Row>
                    
                </Container>
            </div>
        );
    }
}
