import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Admin from './Admin';
import Acceuil from './Acceuil';
import{connect} from "react-redux"
import { showUser, showProduct } from '../actions/actions';
import { bindActionCreators } from 'redux';
import * as serviceUser from "../services/serviceUser";
import * as serviceProduct from "../services/serviceProduct";

class Register extends React.Component {
    constructor(props){
        super(props);

        this.sendForm = this.sendForm.bind(this);
        this.connect = this.connect.bind(this);

        
        this.state = {
            page: true,
            mail:"",
            password:"",
            datasUsers: [],
            check:""

        }
    }

    async componentDidMount(){
        let Users = await serviceUser.allDataUsers();
        let Products = await serviceProduct.allDataProducts();
        
        this.props.dispatch(showUser(Users.data));
        this.props.dispatch(showProduct(Products.data));
            
        this.setState({datasUsers: Users.data});
        
    }

   async sendForm(e){
       serviceUser.createUser(e);
       this.setState({page: "login", 
                     mail: e.target.mail.value, 
                     password: e.target.password.value});
        alert("Created");
        window.location.reload();
    }

    

    async connect(e){
        e.preventDefault();
        
        let getUser = await serviceUser.checkUser(e);
        this.setState({page: null, check: getUser});
    }

    register(){
      return ( 
      <div> 
          {this.state.page === false && localStorage.getItem("user") === null &&
            <Container>
                <Form onSubmit={this.sendForm}>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="text" name="mail" ref="mail" placeholder="Enter email" />
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" name="password" ref="password" placeholder="Enter email" />
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" name="passwordVerif" ref="passwordVerif" placeholder="Enter email" />
                </Form.Group>
                    {/* <input placeholder="mail" name="mail" ref="mail" type="text"/> */}
                    {/* <input placeholder="password" name="password" ref="password" type="password"/> */}
                    {/* <input placeholder="password" name="passwordVerif" ref="passwordVerif" type="password"/> */}
                    <Button type="submit">Envoyer</Button>
                </Form>
                <Button type="button" onClick={() => { this.setState({page: true})}}>Loggin</Button>
            </Container>
          }
          {this.state.page === true && localStorage.getItem("user") === null && 
                <Container>
                    <Row>
                        <form onSubmit={this.connect}>
                            <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                            <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="password"/></Col>
                            <Button type="submit">Envoyer</Button>
                        </form>
                    </Row>
                    
                    <Button type="button"  onClick={() => { this.setState({page: false})}}>Register</Button>
                </Container>
            }
            {localStorage.getItem("user") === "admin" && 
                <Container>
                    <button onClick={() => {localStorage.clear();window.location.reload();}}>Deconnect</button>
                    <Admin/>
                </Container>
            }
            {localStorage.getItem("user") !== null && localStorage.getItem("user") !== "admin" && 
                <Container>
                    <Acceuil/>
                </Container>
            }
        </div>)
        
    }
    
    render(){
        return(
            <div>
                <Container>
                    <Row>
                        {this.register()}
                    </Row>
                </Container>
            </div>
        );
    }
}

function mapDispatchToProps(dispatch) {
    return { 
        user: bindActionCreators(showUser, dispatch),
        product: bindActionCreators(showProduct, dispatch)
    }
  }
  
  export default connect(mapDispatchToProps)(Register);
