import React from 'react';
import axios from "axios";
import {connect} from "react-redux";


class Admin extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            datas: [],
            list:[],
            buttonModify: false
        }

        this.listUser = this.listUser.bind(this);
        this.updateUser = this.updateUser.bind(this);
    }

    async componentDidMount(){
        let datas = await this.props.datas;
        let list =  datas.map((value, index) => { 
            return (
                <tr key={index}>
                    <th>{value._id}</th>
                    <th>{value.mail}</th>
                    
                    <td>
                        <button onClick={() => this.updateUser(value._id)}>Modifier</button>
                    </td>
                </tr>
            );
            });
        this.setState({dats:datas, list:list});
    }

