    render(){
        console.log()
        return(
            <table>
                <tbody>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    {this.state.list}
                </tbody>
            </table>
                

        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }
  export default connect(mapStateToProps)(Admin);
