import React from "react";
import {Navbar, Nav, Button, Form, Card, ListGroup} from 'react-bootstrap';
import {Link} from "react-router-dom";
import { connect } from "react-redux";


class NavbarMenu extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            data: [],
            listArtcile:[]
        }

        this.handleChange= this.handleChange.bind(this)
    }

    async componentDidMount(){
        let products = await this.props.products;
        this.setState({data: products});
        this.listArtcile();
    }

    async listArtcile(){
        let listArtcile = await this.state.data.map((value, index) => {
            return(
            <ListGroup.Item key={index}><a href="#">{value.article}</a></ListGroup.Item>
            )
        });
        
        this.setState({listArtcile: listArtcile});
    }

    async handleChange(e){
        console.log(e.target.value)
        return "hahaha";
    }

    render(){
        return(
            <div>
                <Navbar collapseOnSelect className="pl-5" expand="lg" expand="md" bg="dark" variant="dark">
                    <Navbar.Brand className="ml-1" href="#home">Fou Market</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        {/* <Nav className="mr-auto" /> */}
                        <Nav className=" mt-3 mr-auto w-100">>
                                <Form.Group className="w-100" controlId="Search">
                                    <Form.Row>
                                        <Form.Control onChange={this.handleChange} type="text" placeholder="Search..." />
                                    </Form.Row>
                                    <Form.Row>
                                        <ListGroup className="w-100 position-absolute overflow-auto" style={{height: "100px", 'z-index':"1"}}>
                                            {this.state.listArtcile}
                                        </ListGroup>
                                    </Form.Row>
                                </Form.Group>
                        </Nav>
                        <Nav>
                            <Nav.Link><Button>Panier</Button></Nav.Link>
                        </Nav>
                        <Nav >
                            { localStorage.getItem("user") === null &&
                            <div>
                                <Link to="/signin"><Button>login</Button></Link>
                                <Link to="/register"><Button>register</Button></Link>
                            </div>
                                
                            }
                            { localStorage.getItem("user") !== null &&
                                <Link to="/"><Button onClick={() => {localStorage.clear()}}>Deconnect</Button></Link>
                            }
                            
                        </Nav>
                        
                        
                    </Navbar.Collapse>
                </Navbar>
            </div>
        )
    }
}

function mapStateToProps(state)  {
    return {
        products: state.product
    }
}

export default connect(mapStateToProps, null)(NavbarMenu);
