import React from "react";
import axios from "axios";

class Product extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            listArtcile:[],
            data:[]
        }
        
        this.addArticle = this.addArticle.bind(this);
        this.updateListArticle = this.updateListArticle.bind(this);
        this.updateProduct = this.updateProduct.bind(this);
        this.deleteProduct = this.deleteProduct.bind(this);
    }
    async componentDidMount(){
        let articles = await axios.get("http://localhost:1234/product/read");
        console.log(articles);
        let listArtcile = articles.data.map((value, index) => {
            return(
                <tr key={index}>
                    <td>{value._id}</td>
                    <td>{value.article}</td>
                    <td>{value.price}</td>
                    <td>{value.quantity}</td>
                    <td>{value.photo}</td>
                    <td><button onClick={this.updateListArticle}>Modify</button></td>
                    <td><button onClick={() => {this.deleteProduct(value._id)}}>Delete</button></td>
                </tr>
            )
        })
        this.setState({listArtcile:listArtcile, dataArticle:articles.data});
    }
    async addArticle(e){
        e.preventDefault();
        let data = {
            article: e.target.article.value,
            price: e.target.price.value,
            quantity: e.target.quantity.value,
            photo: e.target.photo.value
        }

        await axios.post("http://localhost:1234/product/create", data);
    }

    async updateListArticle(){
        let listUpdate = await this.state.dataArticle.map((value, index) => {
            return(
                <tr key={index}>
                    <td>
                        <form onSubmit={this.updateProduct}>
                            <input name="id" type="text" placeholder={value._id} defaultValue={value._id}/>
                            <input name="article" type="text" placeholder={value.article} defaultValue={value.article}/>
                            <input name="price" type="text" placeholder={value.price} defaultValue={value.price}/>
                            <input name="quantity" type="text" placeholder={value.quantity} defaultValue={value.quantity}/>
                            <input name="photo" type="text" placeholder={value.photo} defaultValue={value.photo}/>
                            <input type="submit" value="Update"/>
                        </form>
                    </td>
                    <td><button onClick={() => {this.deleteProduct(value._id)}}>Delete</button></td>
                </tr>
            )
        });

        this.setState({listArtcile:listUpdate});
    }

    async updateProduct(e){
        e.preventDefault();
        let data = {
            id: e.target.id.value,
            article: e.target.article.value,
            price: e.target.price.value,
            quantity: e.target.quantity.value,
            photo: e.target.photo.value
        }
        
        await axios.put("http://localhost:1234/product/update", data);
    }

    async deleteProduct(id){
        let data = {
            id: id
        }
        await axios.delete("http://localhost:1234/product/delete", {data});

    }

    render(){
        return(
            <div>
                <form onSubmit={this.addArticle}>
                    <input name="article" type="text" placeholder="article"/><br></br>
                    <input name="price" type="text" placeholder="price"/><br></br>
                    <input name="quantity" type="text" placeholder="quantity"/><br></br>
                    <input name="photo" type="text" placeholder="photo"/><br></br>
                    <input value="Ajouter" type="submit"/>
                </form>
                <table>
                <thead>
                        <tr>
                            <th>Id</th>
                            <th>article</th>
                            <th>price</th>
                            <th>quantity</th>
                            <th>photo</th>
                        </tr>
                        
                    </thead>
                    <tbody>
                        {this.state.listArtcile}
                    </tbody>
                </table>
                
            </div>
            
        )
    }
}

export default Product;
