import React from 'react';
import axios from "axios"
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';


export default class Product extends React.Component {
    constructor(props){
        super(props);

        this.sendForm = this.sendForm.bind(this);
        this.connect = this.connect.bind(this);
        this.state = {
            sendBool: true,
            mail:"",
            password:""

        }
        
    }

    async componentDidMount(){
        console.log(await axios.get("http://localhost:1234/loggin/read"))
    }

   async sendForm(e){
       e.preventDefault();

       let data = {
           mail: this.refs.mail.value,
           password: this.refs.password.value
       }
        
        await axios.post("http://localhost:1234/loggin/create", data);
        console.log(await axios.get("http://localhost:1234/loggin/read"));
        this.setState({sendBool: false, 
                        mail: this.refs.mail.value, 
                        password: this.refs.password.value});
    
    }

    async connect(e){
        e.preventDefault();
        let dataUser = await axios.get("http://localhost:1234/loggin/read");

        dataUser.data.map((value, index) => {
            console.log(value._id);
            if(this.state.mail === value._id && this.state.password === value.password){
                console.log("ok")
            }
        });

        
    }

    register(){
       let register = <form onSubmit={this.sendForm}>
                            <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                            <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="text"/></Col>
                            <Col xs md lg="10"><input placeholder="password" name="passwordVerif" ref="passwordVerif" type="text"/></Col>
                            <Button type="submit">Envoyer</Button>
                        </form>

        return register;
    }

    loggin(){
        let loggin = <form onSubmit={this.connect}>
                        <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                        <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="text"/></Col>
                        <Button type="submit">Envoyer</Button>
                    </form>
        return loggin;
    }

    
    render(){
        console.log(this.state.sendBool);
        return(
            <div>
                <p>Component Users</p>
                <Container>
                    <Row>
                    { this.state.sendBool ? this.register(): this.loggin()}
                        
                    </Row>
                    
                </Container>
            </div>
        );
    }
}
