import React from 'react';
import axios from "axios";
import {connect} from "react-redux";


class Admin extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            datas: [],
            list:[],
            buttonModify: false
        }

        this.updateUser = this.updateUser.bind(this);
    }

    async componentDidMount(){
        console.log(this.props.datas);
        let datas = await this.props.datas;
        let list =  datas.map((index, value) => { 
            return (
            <tr >
                <th key={index}></th>
                {/* <th>{value._id}</th>
                <th>{value.mail}</th>
                
                <td>
                    <button onClick={() => this.updateUser(value._id)}>Modifier</button>
                </td> */}
            </tr>
            );
            });
        this.setState({dats:datas, list:list});
    }

    updateUser(id){
       let UpdateList =  this.state.datas.map((index, value) => {
            return(
                <tr key={index}>
                    <th>{value._id}</th>
                    <th>{value.mail}</th>
                    <td>
                        <button onClick={this.updateUser(true)}>Modifier</button>
                    </td>
                </tr>);
        })
        this.setState({list: UpdateList})
    }


    render(){
        console.log()
        return(
            <table>
                <tbody>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    {this.state.list}
                </tbody>
            </table>
                

        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }
  export default connect(mapStateToProps)(Admin);
