import React from "react";
import { Form } from "react-bootstrap"
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";

class Filter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            list: [],
            activeCategory: false
        }

        this.activeCategory = this.activeCategory.bind(this);
    }

    async componentDidMount(){
       let products = await this.props.products;
       this.setState({data: products});
       this.categoryFilter();
    }

    async categoryFilter(){
        const type = 'radio'
        let list =  await this.state.data.map((value, index) => {
            if(value.category !== undefined || null){
                return (
                    <Form.Check onClick={() => {this.activeCategory(value.category);}} key={index} label={value.category}  id={`inline-${type}-1`} />
                )
            }
            
        })
        this.setState({list: list});
    }

    activeCategory(category){
        localStorage.removeItem('categoryFilter');
        localStorage.setItem('categoryFilter', category);
        this.props.history.push(`/Accueil/${category}`)
    }

    render() {
        return (
            <Form className="mt-3">
                <Form.Group controlId="exampleForm.ControlSelect2">
                    <Form.Label>Filter</Form.Label>
                    <Form.Control as="select">
                        <option>Default</option>
                        <option>Price low</option>
                        <option>Price High</option>
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Category</Form.Label>
                    {['radio'].map((type) => (
                        <div key={`default-${type}`} className="mb-3">
                            {this.state.list}
                        </div>
                    ))}
                </Form.Group>
            </Form>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(withRouter(Filter));
