import React from "react";
import { connect } from "react-redux";
import "../style/Acceuil.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, Row, Col, Card, Button} from 'react-bootstrap';
import Header from "./Header";
import Filter from "./Filter";


class Acceuil extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            list: []
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount() {
        let products = await this.props.products;
        this.setState({ data: products });
        this.listData();
        console.log("ok");
    }


    async listData() {
        
        let list = this.state.data.map((value, index) => {
            console.log(localStorage.getItem("categoryFilter"))
            if(value.category === localStorage.getItem("categoryFilter")){
                return (
                    <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                        <Card.Img variant="pic" src={"./img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                        <Card.Body>
                            <Card.Title>{value.article}</Card.Title>
                            <Card.Text>{value.price}$</Card.Text>
                            <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                            <Button variant="primary">Add cart</Button>
                        </Card.Body>
                    </Card>
                )
            }
            return (
                <Card className="border border-secondary mt-3 mr-3" key={index} style={{ width: '13em' }}>
                    <Card.Img variant="pic" src={"./img/" + value.photo}  style={{ width: "auto", height: "200px" }}/>
                    <Card.Body>
                        <Card.Title>{value.article}</Card.Title>
                        <Card.Text>{value.price}$</Card.Text>
                        <Card.Text>description: {value.article} blablablablablablablablablalba</Card.Text>
                        <Button variant="primary">Add cart</Button>
                    </Card.Body>
                </Card>
            )
        });

        this.setState({ list: list })
    }

    render() { 

        return (
            <div>
                <Header />
                <Container>
                    <Row>
                        <Col xs="2" sm="2" md="3" lg="2" className="border border-secondary mt-3 ml-3"><Filter/></Col>
                        <Col><div className="flex">{this.state.list}</div></Col>
                    </Row>
                </Container>
            </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Acceuil);
