import React from 'react';
import axios from "axios"
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';


export default class Product extends React.Component {
    constructor(props){
        super(props);

        this.sendForm = this.sendForm.bind(this);
        this.connect = this.connect.bind(this);
        this.state = {
            sendBool: true,
            mail:"",
            password:""

        }
        
    }

    async componentDidMount(){
        console.log(await axios.get("http://localhost:1234/loggin/read"))
    }

   async sendForm(e){
       e.preventDefault();

       let data = {
           mail: this.refs.mail.value,
           password: this.refs.password.value
       }
        
        await axios.post("http://localhost:1234/loggin/create", data);
        console.log(await axios.get("http://localhost:1234/loggin/read"));
        this.setState({sendBool: false, 
                        mail: this.refs.mail.value, 
                        password: this.refs.password.value});
    
    }

    async connect(e){
        e.preventDefault();

        let data = {
            mail: this.refs.mail.value,
            password: this.refs.password.value
        }
        let verify = await axios.post("http://localhost:1234/loggin/verify", data);

        console.log(verify);
        let dataUser = await axios.get("http://localhost:1234/loggin/read");

        

        dataUser.data.map((value, index) => {
            if(this.state.mail === value.mail && this.state.password === value.password){
                console.log("ok")
            }
        });
    }

    register(){
      return ( 
      <div> 
          {this.state.sendBool && 
            <div>
                <form onSubmit={this.sendForm}>
                    <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                    <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="text"/></Col>
                    <Col xs md lg="10"><input placeholder="password" name="passwordVerif" ref="passwordVerif" type="password"/></Col>
                    <Button type="submit">Envoyer</Button>
                </form>
                <Button type="button" onClick={() => { this.setState({sendBool: false})}}>Loggin</Button>
            </div>
          }
          {!this.state.sendBool && 
                <div>
                    <form onSubmit={this.connect}>
                                    <Col xs md lg="10"><input placeholder="mail" name="mail" ref="mail" type="text"/></Col>
                                    <Col xs md lg="10"><input placeholder="password" name="password" ref="password" type="password"/></Col>
                                    <Button type="submit">Envoyer</Button>
                    </form>
                    <Button type="button"  onClick={() => { this.setState({sendBool: true})}}>Register</Button>
                </div>
            }
        </div>)
    }
    
    render(){
        return(
            <div>
                <p>Component Users</p>
                <Container>
                    <Row>
                    {this.register()}
                    </Row>
                    
                </Container>
            </div>
        );
    }
}
