import React from "react";
import { connect } from "react-redux";
import "../style/Acceuil.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container} from 'react-bootstrap';
import Header from "./Header"


class Acceuil extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            list: []
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount() {
        let products = await this.props.products;
        this.setState({ data: products });
        console.log(products);
        this.listData();

    }

    async listData() {
        let list = this.state.data.map((value, index) => {
            return (
                <div className="card" key={index}>
                    <img src={"./img/" + value.photo} alt="image" style={{ width: "200px", height: "200px" }} />
                    <div>
                        <p>{value.article}</p>
                        <p>{value.price}$</p>
                    </div>
                </div>
            )
        });

        this.setState({ list: list })
    }


    render() {
        return (
            <Container>
                <Header />
                <div className="flex">
                    {this.state.list}
                </div>
            </Container>

        )
    }

}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Acceuil);
