import React from "react";
import { connect } from "react-redux";
import "../style/Acceuil.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, Row, Col, Card, Button} from 'react-bootstrap';
import Header from "./Header"


class Acceuil extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            list: []
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount() {
        let products = await this.props.products;
        this.setState({ data: products });
        console.log(products);
        this.listData();

    }

    async listData() {
        let list = this.state.data.map((value, index) => {
            return (
                <Card key={index} style={{ width: '5rem' }}>
                    <Card.Img variant="pic" src={"./img/" + value.photo} style={{ width: "auto", height: "100px" }}/>
                    <Card.Body>
                        <Card.Title>{value.article}</Card.Title>
                        <Card.Text>{value.price}$</Card.Text>
                        <Button variant="primary">Add cart</Button>
                    </Card.Body>
                </Card>
            )
        });

        this.setState({ list: list })
    }


    render() {
        return (
            <Container>
                <Header />
                <Row>
                    <Col xs md lg="2"><div className="test"></div></Col>
                    <Col>
                        <div className="flex">{this.state.list}</div>
                    </Col>
                </Row>
                
            </Container>

        )
    }

}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Acceuil);
