import React from 'react';
import Accueil from "./component/Accueil"
import Register from "./component/Register"
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useParams,
  useHistory
} from "react-router-dom";
import {Container} from 'react-bootstrap';

// const {category } = useParams();
// console.log(this.props.hisotry);
const history = useHistory()
console.log(hisotry);

function App() {
  return (
    <Container fluid>
      <Router>
        <Switch>
          <Route exact path="/" render={(props) => <Accueil {...props} category={localStorage.getItem("categoryFilter")}/>}/>
          <Route path="/register"><Register path={{pathname:"register"}}/></Route>/>
          <Route path="/signin"><Register path={{pathname:"signin"}}/></Route>/>
          <Route exact path="/filter/:category" render={(props) => <Accueil {...props} category="all"/>}/>

        </Switch>
      </Router>
    </Container>
  );
}

export default App;
