import React from 'react';
import axios from "axios";
import {connect} from "react-redux";
import { bindActionCreators } from 'redux';
import { deleteUser } from '../actions/actions';


class Admin extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            datas: [],
            listUser:[],
            buttonModify: false,
            listArticle:[]
        }

        this.listUser = this.listUser.bind(this);
        this.updateListUser = this.updateListUser.bind(this);
        this.sendUpdate = this.sendUpdate.bind(this);
        this.splice = this.splice.bind(this);
        this.listProduct = this.listProduct.bind(this);
    }

    async componentDidMount(){
        let datas = await this.props.datas;
        this.setState({datas:datas});
        this.listUser();
    }


    async listUser(){
        
        let list =  await this.state.datas.map((value, index) => { 
            return (
                    
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                        <td>
                            <button onClick={this.updateListUser}>Modify</button>
                        </td>
                        <td>
                            <button onClick={() => {this.splice(value._id)}}>Delete</button>
                        </td>
                    </tr>
                        
                );
            });
            this.setState({listUser:list});
    }

    async listProduct(){
        
        let list =  [];
        this.setState({listArticle:list});
    }

    splice(id){
        var tab = [];
        tab.push(id);
        for (var i = 0; i < this.state.datas.length; i++) {
            var obj = this.state.datas[i];

            if (tab.indexOf(obj._id) !== -1) {
                this.state.datas.splice(i, 1);
            }
        }
        
        this.props.actions(id, this.state.datas);
        this.setState({data: this.state.datas});
        this.listUser();
    }

    async updateListUser(){
       
        let list =  await this.state.datas.map((value, index) => { 
            return (
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                        <td>
                            <form onSubmit={(e) => {this.sendUpdate(e);this.listUser();}}>
                                <input  type="hidden" value= {value._id} name="id"/>  
                                <input  type="text"  name="password"/>
                                <input  type="submit"  value="Update"/>    
                            </form>
                            
                        </td>
                    </tr>
                );
            });
            this.setState({listUser:list});
    }

    async sendUpdate(e){
        e.preventDefault();
        let data = { 
            id: e.target.id.value,
            password: e.target.password.value
            
        }
        await axios.put("http://localhost:1234/loggin/update", data);
    }

   
    render(){
        console.log(this.state.buttonModify);
                return(
            <div>
                <button onClick={() => {localStorage.clear();window.location.reload();}}>Deconnect</button>
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                        </tr>
                        
                    </thead>
                    <tbody>
                        {this.buttonModify && this.state.listUser}
                        {!this.buttonModify && this.state.listArticle}
                    </tbody>
                    
                </table>
            </div>
            
        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }

//   function mapDispatchToProps(dispatch){
//     return { actions: bindActionCreators(deleteUser, dispatch)};
    
// }
function mapDispatchToProps(dispatch) {
  return { actions: (id, data) => {dispatch(deleteUser(id, data))} }
}
  export default connect(mapStateToProps, mapDispatchToProps)(Admin);
