import React from "react";
import {connect} from "react-redux";
import "../style/Acceuil.css"
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

class Acceuil extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            data: [],
            list:[]
        }

        this.listData = this.listData.bind(this);
    }

    async componentDidMount(){
       let products = await this.props.products;
       this.setState({data:products});
       this.listData();
       
    }

    async listData(){
        let list = this.state.data.map((value, index) => {
            console.log(value);
            return(
            <div className="card" key={index}>
                <img src={"/img/"+value.photo} alt="image" style={{width:"200px",height:"200px"}}/>
                <p>{value.article}</p>
                <p>{value.price}$</p>
            </div>
            )
        });

        this.setState({list: list})
    }


    render(){
        console.log(this.state.list);
        return(
            <Container>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand href="#home">Navbar</Navbar.Brand>
                    <Nav className="mr-auto">
                        <Nav.Link href="#home">Home</Nav.Link>
                        <Nav.Link href="#features">Features</Nav.Link>
                        <Nav.Link href="#pricing">Pricing</Nav.Link>
                    </Nav>
                    <Form inline>
                        <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                        <Button variant="outline-info">Search</Button>
                    </Form>
                </Navbar>
                <h3>Bienvenue</h3>
                <div className="flex">
                    {this.state.list}
                </div>
            </Container>
            
        )
    }

}

const mapStateToProps = (state) => {
    return {
        products: state.product
    }
}
export default connect(mapStateToProps, null)(Acceuil);
