    //                 <th>{value.mail}</th>
    //                 <td>
    //                     <input onClick={} type="text" value={value.password} name="password"/>
    //                 </td>
    //             </tr>);
    //     })
    //     this.setState({list: UpdateList})
    }

    listUser(){
        let list =  this.state.datas.map((value, index) => { 
            return (
                    <tr key={index}>
                        <th>{value._id}</th>
                        <th>{value.mail}</th>
                        {this.state.buttonModify && 
                            <td>
                                <button onClick={() => {this.setState({buttonModify: true});this.listUser(this.state.datas);}}>Modifier</button>
                            </td>}

                        {!this.state.buttonModify && 
                            <td>
                                <input  type="text" value={value.password} name="password"/>
                            </td>}
                    </tr>
                );
            });
        this.setState({list:list});
    }
    render(){
        console.log(this.state.buttonModify)
        return(
            <table>
                <tbody>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                    </tr>
                    {this.state.list}
                </tbody>
            </table>
                

        )
    }
}

function mapStateToProps(state){
    return {
      datas: state.reducer,
    };
  }
  export default connect(mapStateToProps)(Admin);
